let initial_state = {
    username: "",
    email: "",
    password: ""
}

const RegisterReducer = (state = initial_state, action) => {
    switch(action.type)
    {
        case 'UPDATE_EMAIL_TEXT':
            state.email_text = action.text;
            return state;
        case 'UPDATE_USERNAME_TEXT':
            state.username_text = action.text;
            return state;
        case 'UPDATE_PASSWORD_TEXT':
            state.password_text = action.text;
            return state;
        default:
            return state;
    }
}

export default RegisterReducer;
