let initial_state = {
    username: ""
}

const HeaderReducer = (state = initial_state, action) => {
    switch(action.type)
    {
        case 'ENTER':
            state.username = action.username;
            return state;
        case 'QUIT':
            state.username = "";
        default:
            return state;
    }
};

export default HeaderReducer;
